(8095, 20187, '_our_strengths_0_title', 'field_5fc443104db2f'),
(8096, 20187, 'our_strengths_0_description', 'Tracking the changes in soil moisture provides valuable insights into the complex interactions between plants, soils and the weather. Sentek technologies helps you see what is truly happening, to support better decision making.'),
