(8107, 20187, '_our_strengths_2_title', 'field_5fc443104db2f'),
(8108, 20187, 'our_strengths_2_description', 'Sentek products are sold and supported by a network of highly trained dealers. They are your local experts in site selection, installation, setup, ongoing product care and data analysis.'),
