(9228, 20343, '_our_strengths_0_icon', 'field_5fc442f74db2e'),
(9229, 20343, 'our_strengths_0_title', 'Measurement of Soil Water, Salinity and Temperature'),
(9230, 20343, '_our_strengths_0_title', 'field_5fc443104db2f'),
(9231, 20343, 'our_strengths_0_description', 'Tracking the changes in soil moisture provides valuable insights into the complex interactions between plants, soils and the weather. Sentek technologies helps you see what is truly happening, to support better decision making.'),
(9232, 20343, '_our_strengths_0_description', 'field_5fc443254db30'),
(9233, 20343, 'our_strengths_1_icon', '20203'),
