(9235, 20343, 'our_strengths_1_title', 'High Resolution Data'),
(9236, 20343, '_our_strengths_1_title', 'field_5fc443104db2f'),
(9237, 20343, 'our_strengths_1_description', 'Sentek soil moisture sensors provide a high data resolution, with changes in moisture of >0.01 % able to be detected. This means that movement of water through the profile, and plant uptake, can be tracked to a scientific standard.'),
(9238, 20343, '_our_strengths_1_description', 'field_5fc443254db30'),
(9239, 20343, 'our_strengths_2_icon', '20205'),
