(9241, 20343, 'our_strengths_2_title', 'Quality Local Knowledge and Support'),
(9242, 20343, '_our_strengths_2_title', 'field_5fc443104db2f'),
(9243, 20343, 'our_strengths_2_description', 'Sentek products are sold and supported by a network of highly trained dealers. They are your local experts in site selection, installation, setup, ongoing product care and data analysis.'),
(9244, 20343, '_our_strengths_2_description', 'field_5fc443254db30'),
(9245, 20343, 'our_strengths_3_icon', '20206'),
(9246, 20343, '_our_strengths_3_icon', 'field_5fc442f74db2e'),
(9247, 20343, 'our_strengths_3_title', 'Site Calibration'),
(9248, 20343, '_our_strengths_3_title', 'field_5fc443104db2f'),
(9249, 20343, 'our_strengths_3_description', 'Each individual sensor on a Sentek probe contains a default calibration equation, providing a robust universal measure of soil water content. For increased accuracy, site specific calibration equations from Sentek’s library can be applied.'),
(9250, 20343, '_our_strengths_3_description', 'field_5fc443254db30'),
(9251, 20343, 'our_strengths_4_icon', '20342'),
