(9253, 20343, 'our_strengths_4_title', 'Innovative and Reliable Technology'),
(9254, 20343, '_our_strengths_4_title', 'field_5fc443104db2f'),
(9255, 20343, 'our_strengths_4_description', 'We are serious about product development and testing, using field and laboratory trials. Sentek products are designed to meet the highest standards, and our manufacturing processes are focused on quality.'),
