(9259, 20343, 'our_strengths_5_title', 'Site and Data Integrity'),
(9260, 20343, '_our_strengths_5_title', 'field_5fc443104db2f'),
(9261, 20343, 'our_strengths_5_description', 'Sentek probes are installed using carefully designed tools to ensure a good fit between the probe exterior and the soil. With no air gaps and no damage to the surrounding profile, data quality is assured.'),
(9262, 20343, '_our_strengths_5_description', 'field_5fc443254db30'),
