(13553, 20875, 'tabs_0_tab_content_two_col_first', '<h3>EnviroSCAN Screw Cap</h3>\r\n<ul>\r\n 	<li>Top cap protrudes above the soil surface</li>\r\n 	<li>Easy to find and readily accessible</li>\r\n 	<li>Tightly sealed with a rubber O-ring</li>\r\n 	<li>Simply unscrews, giving easy access to the probe for servicing</li>\r\n</ul>'),
(13554, 20875, '_tabs_0_tab_content_two_col_first', 'field_60124cbf21636'),
(13555, 20875, 'tabs_0_tab_content_two_col_second', '<h3>EnviroSCAN Flat Cap</h3>\r\n<ul>\r\n 	<li>Allows for the probe to sit flush with the ground surface</li>\r\n 	<li>Water-tight, 3 point sealing design</li>\r\n 	<li>Provides ready access to the probe for servicing</li>\r\n 	<li>Avoids potentially costly damage from machinery</li>\r\n</ul>'),
